import React from 'react'
import Header from './components/Header'
import ProfileImage from './components/ProfileImage';
import defaultImage from "./img/default-avatar.jpg";


function CompanyBio() {


    return (
        <>
            <Header />
            <div className='wrapper'>
                <h1>Name</h1> 
                <form>
                    <fieldset>
                        <p>
                            <label>
                                <p>Company Name</p>
                                <input type="text" />
                            </label>
                        </p>
                        <p>
                            <label>
                                <ProfileImage />
                                <br></br>
                                <input id="upload_img" onchange="loadImage()" accept="image/jpeg, image/png" type="file" />   
                            </label>
                        </p>

                    </fieldset>
                    <button type='submit'>Save</button>
                </form>
            </div> 

        </>
    )
}
const image = document.getElementById("image");

function replaceEmptySrc(imageFile) {
    if (image.getAttribute('src') === '') {
       image.src = './img/default-avatar.jpg"';
    }
}


//setup default profile picture

//load profile image upon upload
function loadImage(){
    var img = document.getElementById("upload_img").value;
    var filename = img.match(/[^\\/]*$/)[0];
    document.getElementById("image").src = "../img/"+filename;
}


export default CompanyBio;
