import React from 'react'
import Header from './components/Header'

function CompanyBio() {

    return (
        <>
            <Header />
            <div className='wrapper'>
                <h1>Name</h1> 
                <form>
                    <fieldset>
                        <label>
                            <p>Company Name</p>
                            <input type="text" />
                        </label>


                    </fieldset>
                    <button type='submit'>Save</button>
                </form>
            </div>
        </>
    )
}

//setup default profile picture
if(document.getElementById("image").src == ""){
    document.getElementById("image").src ="../img/default-avatar.jpg";
}
//load profile image upon upload
function loadImage(){
    var img = document.getElementById("upload_img").value;
    var filename = img.match(/[^\\/]*$/)[0];
    document.getElementById("image").src = "../img/"+filename;
}


export default CompanyBio;
